import "./App.css";
import React, {useState,useEffect} from 'react'; 
import { Web3ReactProvider, useWeb3React } from '@web3-react/core'
import Web3 from 'web3'
import { injected } from "./wallet/Connect";
import StringMan from "../../build/contracts/StringMan.json";

const { active, account, chainId, library, activate,deactivate } = useWeb3React()

const networkId = chainId;
const abi = StringMan.abi;
const address = StringMan.networks[networkId].address;
const contract = new Web3.et.Contract(abi,address);

const [length ,setLength] = useState(null);



function App() {



  async function connect () {
    try {
      await activate(injected);
    }
    catch (ex){
      console.log(ex);
    }
  }

  async function calculateLength (e) {
    e.preventDefault();
    const length = await contract.methods.calculateLength(e.target.value).call({from:account});
    setLength(length);
  }



  return (

      <div className="container">
    <h1 className="text-center"> String manipulation</h1>
    <div className="text-center">
     {active?<button className="btn btn-warning text-center "> Connected with {account}</button>:<button className="main-mint-btn text-center" onClick={connect} >Connect</button>}
    </div>
    <div classname ="row">
       <div className = "col-sm-8">
        <h2> Length</h2>
        <form onSubmit={ (e)=>{calculateLength(e)}}>
          <div className ="form-group">
            <label htmlFor="string-length">String</label>
            <input type="text" className="form-control" id="string-length"/>
            </div>
            <button type="submit" className="btn btn-info">Submit</button>
            <p> {  `Result: `}</p>
            </form>

          </div>
       </div>

       <br/>

       <div className ="row">
       <div className = "col-sm-8">
        <h2> Concatenate </h2>
        <form>
          <div className ="form-group">
            <label htmlFor="string-1">String 1</label>
            <input type="text" className="form-control" id="string1"/>
            </div>
            <div className ="form-group">
            <label htmlFor="string-2">String 2</label>
            <input type="text" className="form-control" id="string2"/>
            </div>
            <button type="submit" className="btn btn-info">Concatenate</button>
            <p> {`Result: `}</p>
            </form>

          </div>
       </div>

       </div>


  );
}

export default App;
